<script>
import CompBaseView from "./CompBaseView.vue";

export default {
  extends: CompBaseView,
  name: "ItemInfo",
  
  data() {
    return {
      enterButton: true,
      enter: "enter",
    };
  },

  mounted() {
    this.getDb();
    this.enterLeaveButton();
  },

  watch: {
    uuid() {
      this.enterLeaveButton();
    }
  },

  methods:{
    routerHistory(){
      this.$router.back();
    },
    enterLeaveButton(){
      let data = localStorage.getItem('entered-' + this.uuid)
      if(data == 'true'){
        this.enterButton = false;
        this.enter = "";
      }
    },
    enterLeave(enterButton){
      if(enterButton == true){
        localStorage.setItem('entered-' + this.uuid, 'true');
      }else {
        if(this.item.type != 'alumni'){
          localStorage.setItem('entered-' + this.uuid, 'false');
          this.reload();
        }
      }
      
    },
    reload(){
      this.$router.go();
    }
  }
};
</script>

