<style scoped lang="scss">
@import "../scss/custom.scss";

h1{
  color: $color-maintext;
  text-align: center;
  font-weight: $font-weight-heading;
  font-size: 30px;
  line-height: 1.2;
}

h3 {
  font-size: 11px;
  font-family: "Inter";
  font-weight: $font-weight-subheading;
  margin-left: 10px;
  color: $color-lightgrey;
}
.container-iteminfo {
  position: absolute;
  display: flex;
  // justify-content: center;
  align-items: center;
  width: 100%;
  flex-direction: column;
  left: 0;
  right: 0;
  background-color: $color-white;

  .head {
    position: relative;
    top: 13px;
    width: 100%;
    height: auto;
    display: flex;
    flex-direction: column;
    // justify-content: center;
    padding: 0 20px 0 20px; 

    .back {
      @include back-button;
      margin-top: 1px;
    }

    .headline{
      position: relative;
      top: -33px;
      display: flex;
      flex-direction: column;
      align-self: center;
      align-items: center;
      text-align: center;

      .amount-members{
        color: $color-subtext;
        font-weight: $font-weight-maintext;
        font-size: 14px;
        margin-top: 5px;
      }
    }
    .buttons {
      position: relative;
      width: 85%;
      display: grid;
      gap: 15px;
      grid-template-columns: 0.75fr 1.5fr 0.75fr;
      margin-top: -20px;
      place-self: center;

      &.alumni {
        grid-template-columns: 0.5fr 2fr 0.5fr;

        .token {
          padding-left: 5px;
        }
      }

      .button {
        height: 45px;
        grid-column: 1fr;
        width: 100%;
        &.primary {
          width: 100%;
          &.enter {
            grid-column: 2;
          }

          svg {
            width: 25px;
            height: 25px;
            fill: $color-white;
          }
          div {
            color: $color-white;
          }
        }
        &.secondary {
          svg {
            margin-right: -4px;
          }
        }
        svg {
          width: 30px;
          height: 30px;
          fill: $color-primary;
        }
      }
    }
  }
  .content{
    top: 30px;
    display: flex;
    flex-direction: column;
    position: relative;
    padding: 20px 20px 0 20px;
    width: 100%;
    background-color: $color-white;
    box-shadow: inset 0 10px 5px -10px $color-boxshadow;
    .wrapper {
      background-color: $color-lightgrey;
      height: auto;
      width: 100%;
      padding: 15px 20px 20px 20px;
      border-radius: 15px;
      margin-bottom: 20px;
      .heading {
        color: $color-white;
        font-weight: $font-weight-subheading;
        text-transform: capitalize;
        font-size: 13px;
        margin-bottom: 3px;
      }
      .text {
        color: $color-maintext;
        font-weight: $font-weight-maintext;
        // text-align: justify;
        font-size: 15px;
      }

      &.media {
        border: none;
        padding-top: 20px;
        display: flex;
        justify-content: center;

        .text {
          font-weight: $font-weight-subheading;
        }

        .more {
          transform: rotate(270deg);
          border: none;
          background-color: transparent;
          width: 18px;
          height: 18px;
          fill: $color-darkgrey;
          margin-left: auto;
        }
      }
    }
  }
}
</style>
